// Generated by Selenium IDE
const { Builder, By, Key, until } = require('selenium-webdriver')
const assert = require('assert')

describe('deshbordSuccessfulJobs', function() {
  this.timeout(30000)
  let driver
  let vars
  beforeEach(async function() {
    driver = await new Builder().forBrowser('chrome').build()
    vars = {}
  })
  afterEach(async function() {
    await driver.quit();
  })
  it('deshbordSuccessfulJobs', async function() {
    // Login first
    await driver.get("http://localhost/multiflexi/logout.php")
    await driver.manage().window().maximize()
    
    // Handle GDPR consent banner using reusable function
    const handleGdprConsent = require('./gdpr.js');
    await handleGdprConsent(driver);
 
    
    // Perform login using reusable function
    const loginWithAdminCredentials = require('./login.js');
    await loginWithAdminCredentials(driver);
    
    // Wait for login to complete
    await driver.sleep(1000)
    
    // Now test dashboard successful jobs
    await driver.get("http://localhost/multiflexi/main.php")
    await driver.findElement(By.id("imgheight30srcimageslogsvgLogs")).click()
    await driver.findElement(By.linkText("ðŸ“‹ Dashboard")).click()
    await driver.findElement(By.css(".col-md-3:nth-child(1) > .bg-success > .card-body")).click()
    await driver.findElement(By.id("viewsuccessfuljobsbutton")).click()
    
    // Wait for the table to load and select first job in list using CSS selector
    await driver.sleep(1000)
    const firstJobLink = await driver.wait(until.elementLocated(By.css("tbody tr:first-child a")), 5000)
    await firstJobLink.click()
  })
})
